import React from 'react';
import axios from 'axios';
import CardList from '../../CardList';

function SearchBar(props){
 const {onSearch} = props;
 const [characters, setCharacters] = React.useState([]);
 const [search, setSearch] = React.useState('');
 const [loading, setLoading] = React.useState(true);
 const [error, setError] = React.useState(); 


 React.useEffect(()=>{
  const API_PUB = process.env.REACT_APP_API_PUB_KEY;
  const ts = 1;
  const hash = '64e20774a9bc0da1b2783e768cd14567';
  const URL_KEY = `https://gateway.marvel.com/v1/public/characters?ts=${ts}&apikey=${API_PUB}&hash=${hash}`;

 axios.get(URL_KEY)
  .then(characters=>
  {setCharacters(characters.data.data.results.indexOf.slice(0,8));
  })
  .catch((err)=>
  {setError(err);}).finally(()=>{setLoading(false);});
},[])

const charactersArray = Object.values(characters)

if (loading) {
  return <p>Loading...</p>;
}

if (error || !Array.isArray(charactersArray)) {
  return <p>There was an error loading your data!</p>;
}


const filterCharacters = search.length === 0 ? charactersArray : 
 charactersArray.filter((character => character.name.toLowerCase().includes(search.toLowerCase())))

const busqueda = function (e){
  setSearch(e.target.value)
}
const enter = (pedidoApi) => {
  if(pedidoApi.key === "Enter"){
    onSearch(props);
  }

};

  return (
    <div>
        <input className="buscador"  
        type="text"
        placeholder="buscar"
        value={search}
        onChange={ busqueda } 
         onKeyPress={enter}
        />
         <CardList charactersArray={filterCharacters} fallback={"Loading..."}/>

    </div>

  );

}

export default SearchBar;
