import React, { useState } from 'react';


function SearchApp() {
  const [searchTerm, setSearchTerm] = useState("");
  return (
    <div>
      <input
        type="text"
        placeholder="Buscar"
        className="buscador"
        onChange={(event) => {
          setSearchTerm(event.target.value);
        }}
      />
      {Object.keys(this.state.searchTerm)
        .filter(key => this.state.searchTerm[key].main === true)
        .map((key, index) => {
          return <div key={key}>
            <section className="containerGalery">
              <article className="container">
                {character.map(personaje => (
                  <div key={personaje.id}
                    className="imageContainer"
                    style={{
                      background: `linear-gradient(rgba(255,255,255,0.1) 70%,rgba(0,0,0,0.5) 100%) ,
                      linear-gradient(228deg, rgba(0,0,0,0.5) 10%, rgba(0,0,0,0.1) 30%) ,
                      url(${personaje.thumbnail.path}.${personaje.thumbnail.extension}) 
                      `, backgroundSize: "cover"
                    }}>
                    <div className="namePosition">
                      <p className="name">{personaje.name}</p>
                    </div>
                  </div>
                ))
                }
              </article>
            </section>
          </div>
        })}
    </div>

  )

}

export default SearchApp
