import React, {useState, useEffect} from 'react';
import './../components/elements/body/main.css';
import MarvelCard from './marvelCard/marvelCard';
import characters from './api/key'

const CardList = () => {
    const [character, setCharacter] = useState([]);

    useEffect(() => {
        characters.then((characterArrange) =>{
            setCharacter(characterArrange)
        })

    }, [])

    const charactersArray = Object.values(characterArrange)

    if (!charactersArray || charactersArray.length === 0)
    {
        return '';
    } else {
  return ( 
        <main>
            {charactersArray.map((character)=>(
                <MarvelCard
                name={character.name}
                key={character.id}
                thumbnail={character.thumbnail}
                />
            ))}
        </main>

  );
};
}
export default CardList;
