import axios from "axios";

export const getCharactersByName = ({ characterName }) => {
  const API_PUB = process.env.REACT_APP_API_PUB_KEY;
  const ts = 1;
  const hash = '64e20774a9bc0da1b2783e768cd14567';
  const pathUrl = `https://gateway.marvel.com/v1/public/characters?&nameStartsWith=${characterName}&limit=8ts=${ts}&apikey=${API_PUB}&hash=${hash}`;
  let response = '';
  try {
    axios.get(pathUrl).then((characters) => {
      response = characters.data.data.results;
    });
  } catch (err) {
    return err;
  }
  return response;
};
