import React from 'react';
import axios from 'axios';
import CardList from '../../CardList';

function SearchBar(){
  const [characters, setCharacters] = React.useState([]);
 const [search, setSearch] = React.useState("");


 React.useEffect(()=>{
  const API_PUB = process.env.REACT_APP_API_PUB_KEY;
  const ts = 1;
  const hash = '64e20774a9bc0da1b2783e768cd14567';
  const URL_KEY = `https://gateway.marvel.com/v1/public/characters?ts=${ts}&apikey=${API_PUB}&hash=${hash}`;

 axios.get(URL_KEY)
  .then(characters=>{
  setCharacters(characters.data.data.results.slice(0,8));
  }).catch(error=>console.log(error))
},[])

const filterCharacters = search.length === 0 ? characters : 
 characters.filter((character => character.name.toLowerCase().includes(search.toLowerCase())))


  return (
    <div>
        <input className="buscador"  
        type="text"
        placeholder="buscar"
        value={search}
        onChange={(e) => setSearch(e.target.value)} 
        />
      <CardList characters={filterCharacters}/>
    </div>

  );

}

export default SearchBar;
