import React from 'react';
import axios from 'axios';
import './navbar.css'
import Cardlist from '../body/CardList';


function SearchBar({getQuery}){
 const [characters, setCharacters] = React.useState([]);
 const [search, setSearch] = React.useState('');
 const [loading, setLoading] = React.useState(true);
 const [error, setError] = React.useState();
 const [query, setQuery] = useState('')




 React.useEffect(()=>{
  const API_PUB = process.env.REACT_APP_API_PUB_KEY;
  const ts = 1;
  const hash = '64e20774a9bc0da1b2783e768cd14567';
  const URL_KEY = `https://gateway.marvel.com/v1/public/characters?name=${query}ts=${ts}&apikey=${API_PUB}&hash=${hash}`;

 axios.get(URL_KEY)
  .then(characters=>
    {setCharacters(characters.data.data.results.slice(0,8));
  })
  .catch((err)=>
  {setError(err);})
  .finally(()=>
  {setLoading(false);});
},[])

const charactersArray = Object.values(characters)
console.log(charactersArray)
if (loading) {
  return <p>Loading...</p>;
}

if (error || !Array.isArray(charactersArray)) {
  return <p>There was an error loading your data!</p>;
}
const onChange = (q) => {
  setSearch(q)
  getQuery(q)
}

const filterCharacters = search.length === 0 ? charactersArray : 
 charactersArray.filter((character => character.name.toLowerCase().includes(search.toLowerCase())))
/*   handleChange = (e) =>{
  this.setState({searchField:e.target.value})
  } */

  return (
    <div className="containerGalery">
      <nav className="barra"> 
            <div className="logo"/>
            <div className="barraSeparadora">|</div> 
            <div className="lupa" alt="lupa"/> 
            <input className="buscador"  
        type="text"
        placeholder="buscar"
        value={search}
        onChange={(e) => onChange(e.target.value)}
        />
        </nav>

    <span>
    <Cardlist charactersArray={filterCharacters} fallback={"Loading..."}/>
    </span>
    </div>
  );

}

export default SearchBar;
