import React from "react";
import SearchBar from "./components/elements/header/search";
import Cardlist from "./components/elements/body/CardList";
import getCharacters from "./api/marvelApi";


const imageFound = /image_not_available/gi;

class App extends React.Component {
  state = { characters: [], name: null}
  onFormSubmit = (e) => {
    this.getMarvelResponse(null, `&nameStartsWith=${e}`, 0);
  };
  getMarvelResponse = (id, name) => {
    getCharacters({
      id: id,
      name: name,
    }).then(({ characters }) => {
      this.setState(
        {
          characters: characters.filter(
            (chr) => !chr.thumbnail.path.match(imageFound)
          ),
          selectedCard: null,
          name: name,
        },
        () => {
          console.log(this.state.characters);
        }
      );
    });
  };
  onLoadClick = () => {
    getCharacters({
      id: null,
      name: this.state.name,
    }).then(({ characters }) => {
      this.setState(
        {
          characters: this.state.characters.concat(
            characters.filter((chr) => !chr.thumbnail.path.match(imageFound))
          ),
          selectedCard: null,
        },
        () => {
          console.log(this.state.characters);
        }
      );
    });
  };
  render(){
  return (
    <div className="App">
      <SearchBar onFormSubmit={this.onFormSubmit} />
      <Cardlist
      characters={this.state.characters}
      />
    </div>
  );
}
}

export default App;
