import React from "react";

export default function Cardlist({ charactersArray, fallback }) {
  if (!charactersArray || charactersArray.length === 0) {
    return fallback;
  } else {
    return (
      <section>
        <article className="container">
          {charactersArray.map((personaje) => (
            <div
              key={personaje.id}
              className="imageContainer"
              style={{
                background: `linear-gradient(rgba(255,255,255,0.1) 70%,rgba(0,0,0,0.5) 100%) ,
            linear-gradient(228deg, rgba(0,0,0,0.5) 10%, rgba(0,0,0,0.1) 30%) ,
            url(${personaje.thumbnail.path}.${personaje.thumbnail.extension}) 
            `,
                backgroundSize: "cover"
              }}
            >
              <div className="namePosition">
                <p className="name">{personaje.name}</p>
              </div>
            </div>
          ))}
        </article>
      </section>
    );
  }
}
