import './App.css';
import React, {useState} from 'react';
import SearchBar from './components/elements/header/search';
import axios from 'axios';
import Cardlist from './components/elements/body/CardList'


function App() {
  if(<SearchBar.value/> === ""){
  const [items, setItems] = useState([])
  const [isLoading, setIsLoading] = useState(true)



  React.useEffect(() => {
    const API_PUB = process.env.REACT_APP_API_PUB_KEY;
    const ts = 1;
    const hash = '64e20774a9bc0da1b2783e768cd14567';
    const fetchItems = async () => {
      setIsLoading(true)
      const result = await axios(
        `https://gateway.marvel.com/v1/public/characters?&ts=${ts}&apikey=${API_PUB}&hash=${hash}`
        )
      console.log(result.data)

      setItems(result.data.data.results)
      setIsLoading(false)
    }

    fetchItems()
  }, [])


  return (   
    <div className="App">
      <SearchBar />
      <Cardlist isLoading={isLoading} items={items}/>
    </div>

  );
  }else{
    const [items, setItems] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    const [query, setQuery] = useState('')


    React.useEffect(() => {
      const API_PUB = process.env.REACT_APP_API_PUB_KEY;
      const ts = 1;
      const hash = '64e20774a9bc0da1b2783e768cd14567';
      const fetchItems = async () => {
        setIsLoading(true)
        const result = await axios(
          `https://gateway.marvel.com/v1/public/characters?name=${query}&ts=${ts}&apikey=${API_PUB}&hash=${hash}`
          )
        console.log(result.data)

        setItems(result.data.data.results)
        setIsLoading(false)
      }

      fetchItems()
    }, [query])


    return (   
      <div className="App">
        <SearchBar getQuery={(q) => setQuery(q)} />
        <Cardlist isLoading={isLoading} items={items}/>
      </div>

    );

  }
}

export default App;
