import React from "react";
import { Element } from "react-scroll";
import './../assets/CharacterCard.css'

const CharacterDetails = ({ selectedCard }) => {
    if (!selectedCard) {
      return <div></div>;
    }

    const image = selectedCard.comics.items;
    const file = selectedCard.comics.items[0].name;
    const detail = selectedCard.urls.find(
      (url) => url.type === "wiki" || url.type === "detail"
    );

    return (
        <div className="popComics" style={{visibility:'visible'}} >
        <Element id="CharacterDetails" className="containerElement">
        <span className="close" onClick={cerrar}>X</span>
          <h2 className="characterName">{selectedCard.name}</h2>
          <div className="CharacterProfile">
            <span src={`${file}`}></span>
            <img src={`${image}/portrait_uncanny.${file}`} alt=""></img>
            <h3 className="CharacterDesc">
              {selectedCard.description}{" "}
              <a target="_blank" href={detail.url}>
                Read More
              </a>
            </h3>
          </div>
        </Element>
      </div>
    );
  };

  export default CharacterDetails;


  function cerrar(e){
    e = document.getElementsByClassName("popComics");
    e.style.visibility = 'hidden';
}
