import React, { useRef, useEffect} from "react";
import { Element } from "react-scroll";
import './../assets/CharacterCard.css'

const CharacterDetails = ({ selectedCard, showModal, setShowModal }) => {
    const modalRef = useRef();
    const closeModal = e => {
       if (showModal.Ref.current === e.target){
           setShowModal(false);
       }
   }
   const keyPress = useCallback(
    e => {
      if (e.key === 'Escape' && showModal) {
        setShowModal(false);
        console.log('I pressed');
      }
    },
    [setShowModal, showModal]
  );
    if (!selectedCard) {
        return <div></div>;
    }
/*     const image = selectedCard.thumbnail.path;
    const file = selectedCard.thumbnail.extension; */
    const detail = selectedCard.urls.find(
        (url) => url.type === "wiki" || url.type === "detail"
    );
    console.log(detail);
    useEffect(
        () => {
          document.addEventListener('keydown', keyPress);
          return () => document.removeEventListener('keydown', keyPress);
        },
        [keyPress]
      );
    return (
        <>
        {showModal ?(
        <div showModal={showModal} >
            <div className="popComics" onClick={closeModal} ref={modalRef} >
                <Element id="CharacterDetails" className="containerElement">
                    <span className="close" onClick={() => setShowModal(prev => !prev)} >X</span>
                    <h2 className="characterName">{selectedCard.name}</h2>
                    <div className="CharacterProfile">
                        <h3 className="CharacterDesc" >
                            {selectedCard.description}{" "}
                            <a target="_blank" href={detail.url}>
                                <div></div>
                            </a>
                        </h3>
                    </div>
                </Element>
            </div>
        </div> ) : null} 
        </>

    );
};

export default CharacterDetails;
