import React from 'react';



function SearchBar() {
  const { onSearch } = props;
  const [searchTerm, setSearchTerm] = useState("");

  const inputSearch = (e) =>{
    const text = e.target.value
    setSearchTerm(text)
  }

  const handleEnterKeyPressed = (e) =>{
    if (e.key === 'Enter'){
      onSearch(searchTerm)
    }
  }
  return (
    <div>
      <div className="c">
        <input className="input" onChange={inputSearch} 
        onKeyPress={handleEnterKeyPressed} type="text" 
        value={searchTerm} placeholder="buscar"/>
      </div>
    </div>

  );

}

export default SearchBar;
