import './App.css';
import React, {useState, useEffect} from 'react';
import SearchBar from './components/elements/header/search';
import axios from 'axios';
import Cardlist from './components/elements/body/CardList'


function App({characterName}) {
  const [items, setItems] = useState([])
  const [isLoading, setIsLoading] = useState(true)
  const [query, setQuery] = useState('')

  useEffect(() => {
    const API_PUB = process.env.REACT_APP_API_PUB_KEY;
    const ts = 1;
    const hash = '64e20774a9bc0da1b2783e768cd14567';
    const fetchItems = async () => {
      setIsLoading(true)
      const result = await axios(
        `https://gateway.marvel.com/v1/public/characters?&nameStartsWith=${characterName}&limit=8ts=${ts}&apikey=${API_PUB}&hash=${hash}`
        )

      setItems(result.data)
      setIsLoading(false)
    }

    fetchItems()
  }, [query])


  return (   
    <div className="App">
      <SearchBar getQuery={(q) => setQuery(q)} />
      <Cardlist isLoading={isLoading} items={items}/>
    </div>

  );
}

export default App;
