import './App.css';
import Personajes from './components/elements/body/main';
import Navbar from './components/elements/header/navbar';
import CardList from './components/CardList';
import React, {useState} from 'react';
import axios from 'axios';

function App() {
  const [character, setCharacters] = useState([])
  const ts = 1;
  const hash = '64e20774a9bc0da1b2783e768cd14567';


  React.useEffect(()=>{
    const API_PUB = process.env.REACT_APP_API_PUB_KEY;
    const URL_KEY = `https://gateway.marvel.com/v1/public/characters?ts=${ts}&apikey=${API_PUB}&hash=${hash}`;

    axios.get(URL_KEY)
    .then(characters=>{
    setCharacters(characters.data.data.results.slice(0,8));
    }).catch(error=>console.log(error))
  },[])

  return (

    <div className="App">
      <header className="App-header">
      <Navbar></Navbar>
      </header>
      <main className="body">
        <CardList results={character.results}></CardList>
      <Personajes></Personajes>
      </main>
    </div>

  );
}

export default App;
